                    }
                    break;
                }
            }
            foreach (self::$flagtypes as $index => $flagtype) {
                if ($flagtype === $other->data) {
                    $otherindex = $index;
                    if ($other->negative) {
                        $otherindex += 13;
                    }
                    break;
                }
            }
            $result = self::$intersection[26 * $selfindex + $otherindex];
            if ($result === 'set') {
                $result = false;
            }
            if ($result === false || $result === null) {
                return $result;
            } else {
                $res = new qtype_preg_charset_flag;
                if ($result[0] === '-') {
                    $res->set_data(qtype_preg_charset_flag::FLAG, qtype_preg_unicode::substr($result, 1));
