                $str = clone $this->data;
                $str->concatenate($other->data);
                $resstr = new qtype_preg_string('');
                for ($i = 0; $i < $str->length(); $i++) {
                    if (qtype_preg_unicode::strpos($str, $str[$i]) == $i) {
